#!/bin/bash

# Populate Google Cloud Secrets with secure demo values
# VARAi Commerce Studio - Security Remediation

set -e

echo "üîê Populating Google Cloud Secrets with secure demo values..."
echo "============================================================"

PROJECT_ID=$(gcloud config get-value project)

# Function to set secret value
set_secret_value() {
    local secret_name=$1
    local value=$2
    
    echo "Setting value for: $secret_name"
    echo "$value" | gcloud secrets versions add "$secret_name" \
        --project="$PROJECT_ID" \
        --data-file=- || {
        echo "  ‚ùå Failed to set value for secret: $secret_name"
        return 1
    }
    echo "  ‚úÖ Set value for secret: $secret_name"
}

# Stripe Configuration (Test Mode - Demo Keys)
set_secret_value "stripe-publishable-key" "pk_test_